import { useState } from "react"

function ShowHideButton({descRef}){
    let [buttonText, changeButtonText] = useState('show') 
    // visibiliy = descRef.current.style.visibility
    console.log(descRef.current.div)

    function changeText(text){
        if (text==="show"){
            changeButtonText("hide")
            // visibiliy = 'none'
        }
        else{
            changeButtonText("show")
            // visibiliy = 'block'
        }
        
    }
    return (
        <button onClick={()=>changeText(buttonText)}>{buttonText}</button>
    )
}

export default ShowHideButton
