import ShowHideButton from "../components/ShowHideButton"
import "../styles/FAQ.css"
import { useRef } from "react"

function FAQ(){

    const descrRef = useRef(null)
    
    function changeText(text){
        if (text==="show"){
            changeButtonText("hide")
            visibility = 'yellow'
            console.log(visibility)
        }
        else{
            changeButtonText("show")
            visibility = 'blue'
            console.log(visibility)
        }
        
    }

    return(
        <>
        <div className="FAQ-header">
            <h2>About</h2>
            <ShowHideButton funcOnClick={changeText}></ShowHideButton>
        </div>
        <div ref = {descrRef} className="descr">Pariatur anim cillum ea eu pariatur excepteur ipsum anim anim nulla veniam adipisicing magna aute. Labore cupidatat sit consectetur sunt ipsum deserunt mollit deserunt nulla. Nostrud do ut officia magna Lorem pariatur adipisicing ad minim tempor ex magna cupidatat.</div>
        </>
    )
}

export default FAQ
